import { useState } from "react";

function App() {
  const [data, setData] = useState([
    {
      id: 1,
      name: "멋진 셔츠",
      price: 10000,
    },
    {
      id: 2,
      name: "멋진 바지",
      price: 20000,
    },
  ]);
  return (
    <>
      {data.map((item) => {
        return (
          <div>
            <h2>{item.name}</h2>
            <h2>{item.price}</h2>
          </div>
        );
      })}
      <button
        onClick={() => {
          setData([
            ...data,
            {
              id: 3,
              name: "멋진 신발",
              price: 100000,
            },
          ]);
        }}
      >
        데이터 추가
      </button>
    </>
  );
}

export default App;
