import React, { useState } from "react";
import "./App.css";

function App() {
  const [todolist, setTodoList] = useState(["리엑트공부", "아침 운동"]);

  const [newTodo, setNewTodo] = useState([""]);
  return (
    <>
      <input
        onChange={(e) => {
          return setNewTodo(e.target.value);
        }}
      />
      <button
        onClick={() => {
          return setTodoList([...todolist, newTodo]);
        }}
      >
        버튼
      </button>
      {todolist.map((todo) => {
        return <div>{todo}</div>;
      })}
    </>
  );
}

export default App;
