import React, { useState } from "react";
import "./App.css";

function App() {
  const [할일목록, set할일목록] = useState(["리엑트공부", "아침 운동"]);

  const [입력값, 입력값변경] = useState([""]);

  return (
    <>
      <input
        value={입력값}
        onChange={(e) => {
          return 입력값변경(e.target.value);
        }}
      />
      <button
        onClick={() => {
          return set할일목록([...할일목록, 입력값]);
        }}
      >
        버튼
      </button>

      {할일목록.map((todo) => {
        return (
          <div>
            <span>{todo}</span>
            <button>삭제</button>
          </div>
        );
      })}
    </>
  );
}

export default App;
