// Products.js
import React from "react";
import { Link, useLocation, useSearchParams } from "react-router-dom";

export default function Products() {
  const location = useLocation();
  const [searchParams, setSearchParams] = useSearchParams();
  const products = location.state && location.state.products;

  if (!products) {
    return null; // productsÍ∞Ä ÏóÜÏùÑ ÎïåÎäî nullÏùÑ Î∞òÌôòÌïòÏó¨ ÏïÑÎ¨¥ Í≤ÉÎèÑ Î†åÎçîÎßÅÌïòÏßÄ ÏïäÏùå
  }

  return (
    <>
      {/* Header */}

      <div
        style={{
          marginTop: "56px",
          textAlign: "center",
        }}
      >
        <h2>üî• Ïó¨Î¶Ñ Ï∂îÏ≤úÌÖú üî•</h2>
        <button
          onClick={() => {
            setSearchParams({
              sort: "price",
            });
          }}
        >
          Í∞ÄÍ≤©ÏàúÏúºÎ°ú Ï†ïÎ†¨Ìï¥Îûè
        </button>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "24px",
          }}
        >
          {products.map((product, index) => (
            <Link to={`/products/${index + 1}`} key={index}>
              <div
                style={{
                  width: "200px",
                  height: "240px",
                  backgroundColor: "#068FFF",
                }}
              >
                {product.name}
                <div>{product.price}</div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </>
  );
}
