import logo from "./logo.svg";
import "./App.css";

function App() {
  const [선택된도시, 선택한다도시] = usestate("서울");
  const cities = [
    {
      name: "서울",
      desc: "서울은 인구 너무 많음",
    },
    {
      name: "부산",
      desc: "부산은 해운대",
    },
    {
      name: "대구",
      desc: "대구 너무 더워",
    },
  ];
  return (
    <>
      <button
        Onclick={() => {
          선택한다도시("서울");
        }}
      >
        서울
      </button>

      <button
        Onclick={() => {
          선택한다도시("부산");
        }}
      >
        부산
      </button>

      <button
        Onclick={() => {
          선택한다도시("대구");
        }}
      >
        대구
      </button>

      <div>
        <h2> {cities[0].name}</h2>
        <p>{cities[0].dexc}</p>
      </div>
      <div>
        <h2> {cities[1].name}</h2>
        <p>{cities[1].dexc}</p>
      </div>
      <div>
        <h2> {cities[2].name}</h2>
        <p>{cities[2].dexc}</p>
      </div>
    </>
  );
}

export default App;
