// Products.js
import React, { useEffect, useState } from "react";
import { Link, useLocation, useSearchParams } from "react-router-dom";

export default function Products() {
  const location = useLocation();
  const [searchParams, setSearchParams] = useState(useSearchParams());
  const products = location.state ? location.state.products : [];

  const [sortedProducts, setSortedProducts] = useState([]);

  useEffect(() => {
    // Í∞ÄÍ≤©ÏàúÏúºÎ°ú Ï†ïÎ†¨ÌïòÎäî Ìï®Ïàò
    const sortByPrice = () => {
      const sorted = [...products].sort((a, b) => b.price - a.price); // ÎπÑÍµê Ìï®Ïàò ÏàòÏ†ï
      setSortedProducts(sorted);
    };

    // Í≤ÄÏÉâ ÌååÎùºÎØ∏ÌÑ∞Ïóê "sort=price"Í∞Ä ÏûàÏùÑ Í≤ΩÏö∞ Ï†ïÎ†¨ ÏàòÌñâ
    if (searchParams.has("sort") && searchParams.get("sort") === "price") {
      sortByPrice();
    } else {
      setSortedProducts(products);
    }
  }, [location.state, searchParams, products]);

  return (
    <>
      {/* Header */}

      <div
        style={{
          marginTop: "56px",
          textAlign: "center",
        }}
      >
        <h2>üî• Ïó¨Î¶Ñ Ï∂îÏ≤úÌÖú üî•</h2>
        <button
          onClick={() => {
            setSearchParams(new URLSearchParams({ sort: "price" }));
          }}
        >
          Í∞ÄÍ≤©ÏàúÏúºÎ°ú Ï†ïÎ†¨Ìï¥Îûè
        </button>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "24px",
          }}
        >
          {sortedProducts.length > 0 ? (
            sortedProducts.map((product, index) => (
              <Link to={`/products/${index + 1}`} key={index}>
                <div
                  style={{
                    width: "200px",
                    height: "240px",
                    backgroundColor: "#068FFF",
                  }}
                >
                  {product.name}
                  <div>Í∞ÄÍ≤©: {product.price}</div>
                </div>
              </Link>
            ))
          ) : (
            <div>ÏÉÅÌíàÏù¥ ÏóÜÏäµÎãàÎã§.</div>
          )}
        </div>
      </div>
    </>
  );
}
