// Main.js
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Main() {
  const navigate = useNavigate(); // navigate Ìï®Ïàò Í∞ÄÏ†∏Ïò¥.

  const [products] = useState([
    {
      name: "Î©ãÏßÑ Î∞îÏßÄ",
      price: 20000,
      options: [28, 30, 32],
      likes: 100,
    },
    {
      name: "Î©ãÏßÑ ÏÖîÏ∏†",
      price: 10000,
      options: ["small", "medium", "large"],
      likes: 200,
    },
    {
      name: "Î©ãÏßÑ Ïã†Î∞ú",
      price: 30000,
      options: [230, 240, 250, 260, 270],
      likes: 300,
    },
  ]);

  const handleClick = () => {
    navigate("/products", { state: { products } }); // "/products" Í≤ΩÎ°úÎ°ú Ïù¥ÎèôÌïòÎ©¥ÏÑú productsÎ•º stateÎ°ú Ï†ÑÎã¨
  };

  return (
    <>
      {/* Header */}

      {/* main */}
      <main>
        {/* ÏºÄÎü¨ÏÖÄ */}
        {/* ÏÉùÎûµ */}

        <section
          style={{
            marginTop: "56px",
            textAlign: "center",
          }}
        >
          <h2
            onClick={() => {
              alert("Ïù¥ÎèôÌï† Í±¥Í∞Ä?"); // ÌÅ¥Î¶≠ Ïãú ÏïåÎ¶ºÏ∞Ω Ï∂úÎ†•
              navigate("/products"); // "/products" Í≤ΩÎ°úÎ°ú Ïù¥Îèô
            }}
          >
            üî• Ïó¨Î¶Ñ Ï∂îÏ≤úÌÖú üî•
          </h2>
          <button onClick={handleClick}>ÎçîÎ≥¥Í∏∞</button>
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              gap: "24px",
            }}
          >
            {/* ÏÉÅÌíà Ïπ¥Îìú */}
            {products.map((product, index) => (
              <div
                key={index}
                style={{
                  width: "200px",
                  height: "240px",
                  backgroundColor: "#068FFF",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "center",
                  alignItems: "center",
                  padding: "16px",
                  borderRadius: "4px",
                  color: "white",
                  fontSize: "16px",
                }}
              >
                <div>{product.name}</div>
                <div>Í∞ÄÍ≤©: {product.price}</div>
              </div>
            ))}
          </div>
        </section>
        {/* Ï∂îÍ∞ÄÏ†ÅÏù∏ Îç∞Ïù¥ÌÑ∞ */}
        {/* ÏÉùÎûµ */}
      </main>
      {/* footer */}
    </>
  );
}
