import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";

function App() {
  const [개수, 개수변경] = useState(0);
  const 가격 = 10000;
  return (
    <div>
      <div>상품 가격: {가격}원</div>
      <hr></hr>
      <button
        onClick={() => {
          개수변경(개수 - 1);
        }}
      >
        -
      </button>
      <span>상품 개수: {개수}</span>
      <button
        onClick={() => {
          개수변경(개수 + 1);
        }}
      >
        +
      </button>
      <div>10개 사면 무료배송!</div>

      <hr></hr>
      <div>상품 개수 * 가격: {개수 * 가격}</div>
      <hr></hr>
      <div>무료 배송까지 남은 개수: {10 - 개수}</div>
    </div>
  );
}

export default App;
