import "./App.css";
import { useState } from "react";

function Button(props) {
  return (
    <button
      onClick={() => {
        //    선택한다도시(cities[0]);
      }}
    >
      서울
    </button>
  );
}

function App() {
  const cities = [
    {
      name: "서울",
      desc: "서울은 인구 너무 많음",
    },
    {
      name: "부산",
      desc: "부산은 해운대",
    },
    {
      name: "대구",
      desc: "대구 너무 더워",
    },
  ];

  return (
    <>
      <Button 도시={"서울"} />
      <Button 도시={"부산"} />
      <Button 도시={"대구"} />

      {선택된도시 === cities[0] ? (
        <div>
          <h2> {cities[0].name}</h2>
          <p>{cities[0].desc}</p>
        </div>
      ) : null}

      {선택된도시 === cities[1] ? (
        <div>
          <h2> {cities[1].name}</h2>
          <p>{cities[1].desc}</p>
        </div>
      ) : null}

      {선택된도시 === cities[2] ? (
        <div>
          <h2> {cities[2].name}</h2>
          <p>{cities[2].desc}</p>
        </div>
      ) : null}
    </>
  );
}

export default App;
