import React, { useState } from "react";
import { nanoid } from "nanoid";
import "./App.css";

function App() {
  const [할일목록, set할일목록] = useState([
    { id: nanoid(), content: "리액트 공부하기1" },
    { id: nanoid(), content: "리액트 공부하기2" },
    { id: nanoid(), content: "리액트 공부하기3" },
    { id: nanoid(), content: "리액트 공부하기4" },
  ]);
  const [입력값, 입력값변경] = useState("");

  return (
    <>
      <input
        value={입력값}
        onChange={(e) => {
          return 입력값변경(e.target.value);
        }}
      />
      <button
        onClick={() => {
          return set할일목록([...할일목록, { id: nanoid(), content: 입력값 }]);
        }}
      >
        추가
      </button>

      {할일목록.map((todo, i) => {
        return (
          // key에 nanoid 적용
          <div key={todo.id}>
            <span>{todo.content}</span>
            <button
              onClick={() => {
                set할일목록(할일목록.filter((td) => td.id !== todo.id));
              }}
            >
              삭제 {todo.id}
            </button>
            <input></input>
          </div>
        );
      })}
    </>
  );
}

export default App;
