import React, { useState } from "react";
import "./App.css";

function App() {
  const [할일목록, set할일목록] = useState(["리엑트공부", "아침 운동"]);

  const [입력값, setNewTodo] = useState([""]);
  return (
    <>
      <input
        onChange={(e) => {
          return setNewTodo(e.target.value);
        }}
      />
      <button
        onClick={() => {
          return set할일목록([...할일목록, 입력값]);
        }}
      >
        버튼
      </button>
      {할일목록.map((todo) => {
        return <div>{todo}</div>;
      })}
    </>
  );
}

export default App;
